document.addEventListener("DOMContentLoaded",(()=>{const e=document.getElementById("openPopupBtn"),t=document.getElementById("popup"),o=document.querySelector(".close");e.addEventListener("click",(()=>{t.style.display="flex",document.body.classList.add("lock-scroll")})),o.addEventListener("click",(()=>{t.style.display="none",document.body.classList.remove("lock-scroll")})),t.addEventListener("click",(e=>{e.target===t&&(t.style.display="none",document.body.classList.remove("lock-scroll"))}))}));